mapper.delete:

delete from TB_SAF001D
where ATTCH_FILE_GRP_ID = #{attchFileGrpId,jdbcType=VARCHAR}
  and ATTCH_FILE_ID = #{attchFileId,jdbcType=VARCHAR}

mapper.select:

select ATTCH_FILE_GRP_ID, ATTCH_FILE_ID, FILE_NM, FILE_EXT, FILE_LENTH, FILE_PATH, 
FILE_URL, SORT_SN, REGIST_USR_ID, REGIST_DT, UPDT_USR_ID, UPDT_DT, FILE_BLOB
from TB_SAF001D
where ATTCH_FILE_GRP_ID = #{attchFileGrpId,jdbcType=VARCHAR}
  and ATTCH_FILE_ID = #{attchFileId,jdbcType=VARCHAR}
