mapper.delete:

delete from TB_SAF003D
where CATLG_FILE_GRP_ID = #{catlgFileGrpId,jdbcType=VARCHAR}
  and CATLG_FILE_ID = #{catlgFileId,jdbcType=VARCHAR}

mapper.select:

select CATLG_FILE_GRP_ID, CATLG_FILE_ID, FILE_NM, FILE_EXT, FILE_LENTH, FILE_PATH, 
FILE_URL, SORT_SN, REGIST_USR_ID, REGIST_DT, UPDT_USR_ID, UPDT_DT, FILE_BLOB
from TB_SAF003D
where CATLG_FILE_GRP_ID = #{catlgFileGrpId,jdbcType=VARCHAR}
  and CATLG_FILE_ID = #{catlgFileId,jdbcType=VARCHAR}
